import React, { useEffect } from "react";
// import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  fetchProduct,
  getProductError,
  getProductStatus,
} from "../redux/productSlice";

function Table() {
  const dispatch = useDispatch();
  const products = useSelector((state) => state.list.products);
  const productStatus = useSelector(getProductStatus);
  const error = useSelector(getProductError);

  useEffect(() => {
    if (productStatus === "idle") {
      dispatch(fetchProduct);
    }
  }, [productStatus, dispatch]);

  let content;
  if (productStatus === "loading") {
    content = <p>"Loading..."</p>;
  } else if (productStatus === "success") {
    content = products.map((data) => (
      <listProduct key={data.id} products={products} />
    ));
  } else if (productStatus === "failed") {
    content = <p>{error}</p>;
  }

  return (
    <div>
      <div className="tabel-section mt-5">
        <div className="px-5">
          <h1 className="display-5 fw-bold text-center">List Product</h1>
          <table className="table table-striped" id="ListProduct">
            <thead>
              <tr>
                <th scope="col">No</th>
                <th scope="col">Product Name</th>
                <th scope="col">Product Category</th>
                <th scope="col">Image of Product</th>
                <th scope="col">Product Freshness</th>
                <th scope="col">Additional Description</th>
                <th scope="col">Product Price</th>
              </tr>
            </thead>
            {content}
          </table>
          {/* end of table */}
          {/* Table Search */}
          <div className="search-table my-3">
            <input
              type="search"
              name="search-table"
              id="tabelSearch"
              className="form-control"
              placeholder="Search"
            />
          </div>
          {/* End of Table Search */}
          {/* Button Group Table */}
          <div className="button-group-table my-3 float-start">
            <button
              type="button"
              className="btn btn-primary mx-2"
              id="deleteButton"
            >
              Deletion
            </button>
            <button
              type="button"
              className="btn btn-outline-primary mx-2"
              id="searchButton"
            >
              Search
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Table;
